$(document).ready(function(){
    		//获取表单中当前页码的文本框
    		var currentIndexInput = $("#searchForm").find("input[name='currentIndex']");
    		$(".index").click(function(){
    			currentIndexInput.val(1);
    			$("#searchForm").submit();
    		});
    		$(".prev").click(function(){
    			//获取后台传递过来的当前页码
    			var currentindex = $(this).attr("current");
    			currentIndexInput.val(currentindex);
    			$("#searchForm").submit();
    		});
    		$(".next").click(function(){
    			//获取后台传递过来的当前页码
    			var currentindex = $(this).attr("current");
    			currentIndexInput.val(currentindex);
    			$("#searchForm").submit();
    		});
    		$(".end").click(function(){
    			//获取后台传递过来的当前页码
    			var currentindex = $(this).attr("current");
    			currentIndexInput.val(currentindex);
    			$("#searchForm").submit();
    		});
});